let timeLeft = 0;
let timerId = null;
let isRunning = false;

const timeDisplay = document.getElementById('time');
const startStopButton = document.getElementById('startStop');
const resetButton = document.getElementById('reset');
const hoursInput = document.getElementById('hours');
const minutesInput = document.getElementById('minutes');
const secondsInput = document.getElementById('seconds');

// Format time as HH:MM:SS
function formatTime(seconds) {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = seconds % 60;
    return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
}

// Update the display
function updateDisplay() {
    timeDisplay.textContent = formatTime(timeLeft);
}

// Timer tick function
function tick() {
    if (timeLeft > 0) {
        timeLeft--;
        updateDisplay();
    } else {
        stopTimer();
        // Play notification sound
        new Audio('data:audio/wav;base64,').play();
    }
}

// Start or pause timer
function toggleTimer() {
    if (isRunning) {
        stopTimer();
    } else {
        startTimer();
    }
}

// Start timer
function startTimer() {
    if (!isRunning && timeLeft > 0) {
        isRunning = true;
        startStopButton.textContent = 'Pause';
        startStopButton.style.backgroundColor = '#FF9800';
        timerId = setInterval(tick, 1000);
    }
}

// Stop timer
function stopTimer() {
    isRunning = false;
    startStopButton.textContent = 'Start';
    startStopButton.style.backgroundColor = '#4CAF50';
    if (timerId !== null) {
        clearInterval(timerId);
        timerId = null;
    }
}

// Reset timer
function resetTimer() {
    stopTimer();
    updateTimer();
    updateDisplay();
}

// Update timer from input fields
function updateTimer() {
    const hours = parseInt(hoursInput.value) || 0;
    const minutes = parseInt(minutesInput.value) || 0;
    const seconds = parseInt(secondsInput.value) || 0;
    
    timeLeft = (hours * 3600) + (minutes * 60) + seconds;
    updateDisplay();
}

// Handle arrow button clicks
function handleArrowClick(event) {
    const button = event.target;
    const target = button.dataset.target;
    const input = document.getElementById(target);
    const currentValue = parseInt(input.value) || 0;
    const max = parseInt(input.max);
    const min = parseInt(input.min);
    
    if (button.classList.contains('arrow-up')) {
        if (!isNaN(max)) {
            input.value = Math.min(currentValue + 1, max);
        } else {
            input.value = currentValue + 1;
        }
    } else {
        input.value = Math.max(currentValue - 1, min);
    }
    
    updateTimer();
}

// Validate input fields
function validateInput(input) {
    const value = parseInt(input.value);
    const max = parseInt(input.max);
    const min = parseInt(input.min);
    if(isNaN(value)){
        input.value = 0;
    }
    if(!isNaN(max)){
      if(value > max){
        input.value = max;
      }
    }
    if(!isNaN(min)){
      if(value < min){
        input.value = min;
      }
    }
    updateTimer();
}

// Event listeners
startStopButton.addEventListener('click', toggleTimer);
resetButton.addEventListener('click', resetTimer);

// Input validation
hoursInput.addEventListener('change', () => validateInput(hoursInput));
minutesInput.addEventListener('change', () => validateInput(minutesInput));
secondsInput.addEventListener('change', () => validateInput(secondsInput));

// Arrow button controls
document.querySelectorAll('.arrow-up, .arrow-down').forEach(button => {
    button.addEventListener('click', handleArrowClick);
});